import React from "react";
import Slider from "react-animated-slider";
import "react-animated-slider/build/horizontal.css";
import "../css/slider-animations.css";
import "../css/supportStyles.css";
import { useSupports } from "../api.js";
import { NavLink } from "react-router-dom";

//npm install react-animated-slider

export default function Support(){
    const { loading, supports, error } = useSupports();
    if (loading) {
        return <p>Loading...</p>;
    }
    if (error) {
        return <p>Something went wrong: {error.message}</p>;
    }

    return(
    <div className='support'>
        <Slider className="slider-wrapper">
            {supports.map((support, index) => (
                <div key={index}
                     className="slider-content">
                    <div className="inner">
                    <Content key={support.id} {...support} />
                    </div>
                </div>
            ))

            }
            <NavLink to='/home' className={"btn"}>
                Get Started
            </NavLink>
        </Slider>
    </div>
)
}

function Content(support) {
    const {title, body} = support;

    return (
        <div>
            <h1>{title}</h1>
            <h2>{body}</h2>
        </div>
    );
}
