import React from "react";
import Messages from '../../components/chat/Messages';
import "../../chatStyles.css";
import { getMessages, useMessages} from "../../api.js";



export  function Mes() {
    var { loading, messages, error } = useMessages();
    if (loading) {
      return <p>Loading...</p>;
    }
    //if (error) {
    //return <p>Something went wrong: {error.message}</p>;
    // }
  
    // use this to make sure you are getting the right data
    console.log(messages);
  
    messages = [{ id: "wewew" }];
  
    // Display a list of the authors
    return (
      <div>
        <h1>Authors List</h1>
        {messages.map(author => (
          <Author key={author.id} {...author} />
        ))}
      </div>
    );
  }

  function Message(author) {
    const { id, first_name, last_name } = author;
    const [showUpdate, setShowUpdate] = useState(false);
  
    return (
      <div className={`author author-${id}`} key={id}>
        <div className="info">
          ({id}) {first_name} {last_name}
          <Button className={"btn"} onClick={() => setShowUpdate(!showUpdate)}>
            {showUpdate ? "-" : "+"}
          </Button>
        </div>
        <AuthorExtended {...author} showUpdate={showUpdate} />
      </div>
    );
  }



export default function Chat() {
    return (
        <div class="container">
            <div class="chatBox scrollable">
                <h1>list</h1>
            </div>
            <div class="chatList scrollable">
                <Messages />
            </div>
        </div>
    );
}
