import React from 'react';
import ReactDOM from "react-dom";
// import "../css/styles.css";
import "../css/supportStyles.css";

export default function Support() {
    var slideIndex = 1;
    showSlides(slideIndex);

    function plusSlides(n){
        showSlides(slideIndex += n);
    }

    function currentSlide(n) {
        showSlides(slideIndex = n);
    }

    function showSlides(n){
        var i;
        var slides = ReactDOM.findDOMNode("slide");
        var dots = ReactDOM.findDOMNode("dot");

        if (n > slides.length){
            slideIndex = 1;
        }

        if (n < 1){
            slideIndex = slides.length;
        }

        for (i=0; i < slides.length; i++){
            slides[i].state.hide
                // style.display = "none";
        }

        for (i=0; i < dots.length; i++){
            dots[i].className = dots[i].className.replace(" active", "");
        }

        slides[slideIndex-1].style.display = "block";
        dots[slideIndex-1].className += " active";

    }

  return (
        <div className="slideshow-container">
            <div className="slide">
                <div className="content">
                    <h1>Welcome!</h1>
                    <p>After user signs up, our support system will guide into pear service.</p>
                    <b>Pear</b>
                </div>

            </div>

            <div className="slide">
                <div className="content">
                    <h1>2</h1>
                    <b>Pear</b>

                </div>
            </div>

            <div className="slide">
                <div className="content">
                    <h1>3</h1>
                    <b>Pear</b>
                </div>
            </div>

            <div className="slide">
                <div className="content">
                    <h1>Thank you</h1>
                    <form action="/backhome" method="post">
                        <input id="but" type="submit" value="Get Started"></input>
                    </form>
                </div>
            </div>

            <a className="previous" /*onClick={event => {plusSlides(-1)}}*/>&#10094;</a>
            <a className="next" /*onClick={event => {plusSlides(1)}}*/>&#10095;</a>

            <div className="dots">
                <span className="dot" /*onClick={event => {currentSlide(1)}}*/></span>
                <span className="dot" /*onClick={event => {currentSlide(2)}}*/></span>
                <span className="dot" /*onClick={event => {currentSlide(3)}}*/></span>
                <span className="dot" /*onClick={event => {currentSlide(4)}}*/></span>
            </div>
        </div>
  );
}
