import React from "react";
import Messages from '../../components/chat/Messages';
import "../../chatStyles.css";
import { getMessages, useMessages} from "../../api.js";


export default function Chat() {
    var { loading, messages, error } = useMessages();
    if (loading) {
      return <p>Loading...</p>;
    }
    console.log(messages);
  
    messages = [{ id: "wewew" }];
    return (
        <div class="container">
            <div class="chatBox scrollable">
                <h1>list</h1>
            </div>
            <div class="chatList scrollable">
                <Messages />
            </div>
        </div>
    );
}


  
  function Message(message) {
    const {_id, conversationId, senderId, text, image, video, timeSent} = message;
  
    return (
      <section className='join'>
          <div className={`chat-room conversation-${conversationId}`}  key={conversationId}>
            <div className="info">
                {/*{_id}*/}
                <br></br>
                <br></br>
                /*{status}*/
                <br></br>
                <NavLink to="/chat" className="btn">Join</NavLink>
                <br></br>
                {topic}
                <br></br>
                {category}
             </div>
         </div>
      </section>
  );
  }
